int main(void){
    volatile int * pixel_ctrl_ptr = (int *)0xFF203020;

	Paddle paddle = {159, 230, 45, 9, -1, false};
	Paddle pre_paddle = paddle;

	Ball ball = {159, 220, 7, -1, 4, 2, 0, 0xFFFF, 0};
	Ball pre_ball = ball;

    /* set front pixel buffer to start of FPGA On-chip memory */
    *(pixel_ctrl_ptr + 1) = 0xC0000000;
    /* now, swap the front/back buffers, to set the front buffer location */
    wait_for_vsync();
    /* initialize a pointer to the pixel buffer, used by drawing functions */
    pixel_buffer_start = *pixel_ctrl_ptr;

    clear_screen();
    draw_letter(letter_R,YELLOW, 60, 120, 5);
    draw_letter(letter_B,YELLOW, 60, 90, 5);
    draw_letter(letter_I,YELLOW, 60, 150, 5);
    draw_letter(letter_C,YELLOW, 60, 180, 5);
    draw_letter(letter_K,YELLOW, 60, 210, 5);

    draw_letter(letter_R,GREY, 110, 90, 5);
    draw_letter(letter_B,GREY, 110, 60, 5);
    draw_letter(letter_E,GREY, 110, 120, 5);
    draw_letter(letter_A,GREY, 110, 150, 5);
    draw_letter(letter_K,GREY, 110, 180, 5);
    draw_letter(letter_E,GREY, 110, 210, 5);
    draw_letter(letter_R,GREY, 110, 240, 5);

    draw_sprite(170,120,54,81, ps4_map);

    /* set back pixel buffer to start of SDRAM memory */
    *(pixel_ctrl_ptr + 1) = 0xC8000000;
    pixel_buffer_start = *(pixel_ctrl_ptr + 1); // we draw on the back buffer

    clear_screen();
    draw_letter(letter_R,YELLOW, 60, 120, 5);
    draw_letter(letter_B,YELLOW, 60, 90, 5);
    draw_letter(letter_I,YELLOW, 60, 150, 5);
    draw_letter(letter_C,YELLOW, 60, 180, 5);
    draw_letter(letter_K,YELLOW, 60, 210, 5);

    draw_letter(letter_R,GREY, 110, 90, 5);
    draw_letter(letter_B,GREY, 110, 60, 5);
    draw_letter(letter_E,GREY, 110, 120, 5);
    draw_letter(letter_A,GREY, 110, 150, 5);
    draw_letter(letter_K,GREY, 110, 180, 5);
    draw_letter(letter_E,GREY, 110, 210, 5);
    draw_letter(letter_R,GREY, 110, 240, 5);
    
    draw_sprite(170,120,54,81, ps4_map);

    state = layout;

	clear_all_text();
	write_string(30, 40, "Press SPACE To Begin");

	unsigned char key = 0;
	while(1){
		switch (state) {
			case layout:
				get_key(&key);
				if(key == 0x29){
					draw_layout(pixel_ctrl_ptr);
					state = start;
				}
				break;
			case start:
				//erase previous images
				get_key(&key);
				draw_paddle(&pre_paddle, 0);
				draw_ball(&pre_ball, 0);

				if (brick_broke) {//if the brick is broken in the previous frame(-2), also break it in this frame(-1)
					break_brick(broken_brick[0], broken_brick[1], play_map);
					ball.score += score_add(ball.touched_pixel);
					brick_broke = false;
					if (break_all(play_map)) {
						state = win;
						break;
					}
				}				
				//update the images
				pre_paddle = paddle;
				update_paddle(&paddle, key);
				pre_ball = ball;
				update_ball(&ball, &paddle, play_map);

				//draw images
				draw_paddle(&paddle, 0xFFDF);
				draw_ball(&ball, 0xFFFF);
				draw_status(ball.score, ball.life);

				wait_for_vsync(); // swap front and back buffers on VGA vertical sync
				pixel_buffer_start = *(pixel_ctrl_ptr + 1); // new back buffer

				if(key == 0x5A){//press ENTER to pause
					write_string(24, 30, "PAUSED, Press SPACE and Continue");
					state = pause;
				}
				break;
			case pause:
				get_key(&key);
				if(key == 0x29){
                    state = start;
					write_string(24, 30, "                                ");
				}
				break;
			case respawn:
				get_key(&key);
			
				//erase previous images
				draw_paddle(&pre_paddle, 0);
				draw_ball(&pre_ball, 0);

				//update images
				pre_paddle = paddle;
                pre_ball = ball;
                ball.x = paddle.x;

				//draw images
				draw_paddle(&paddle, 0xFFDF);
                draw_ball(&pre_ball, 0);

				wait_for_vsync(); // swap front and back buffers on VGA vertical sync
				pixel_buffer_start = *(pixel_ctrl_ptr + 1); // new back buffer

				if(key == 0x29){
					state = start;
					write_string(30, 30, "                          ");
				}
				break;
			case win:
				clear_screen();
                write_string(2, 2, "                                                                            ");
                draw_letter(letter_Y,YELLOW, 80, 60, 5);
                draw_letter(letter_O,YELLOW, 80, 90, 5);
                draw_letter(letter_U,YELLOW, 80, 120, 5);

                draw_letter(letter_W,GREY, 80, 180, 5);
                draw_letter(letter_I,GREY, 80, 210, 5);
                draw_letter(letter_N,GREY, 80, 240, 5);

                //draw_final_score(ball.score);
				
				wait_for_vsync(); // swap front and back buffers on VGA vertical sync
				pixel_buffer_start = *(pixel_ctrl_ptr + 1); // new back buffer
				break;
			case lose:
				clear_screen();
                write_string(2, 2, "                                                                            ");
                draw_letter(letter_Y,YELLOW, 80, 50, 5);
                draw_letter(letter_O,YELLOW, 80, 80, 5);
                draw_letter(letter_U,YELLOW, 80, 110, 5);

                draw_letter(letter_L,GREY, 80, 170, 5);
                draw_letter(letter_O,GREY, 80, 200, 5);
                draw_letter(letter_S,GREY, 80, 230, 5);
                draw_letter(letter_E,GREY, 80, 260, 5);                

				//draw_final_score(ball.score);

				wait_for_vsync(); // swap front and back buffers on VGA vertical sync
				pixel_buffer_start = *(pixel_ctrl_ptr + 1); // new back buffer
				break;
		}
	}
}
