void plot_pixel(int x, int y, short int line_color) {
    *(short int *)(pixel_buffer_start + (y << 10) + (x << 1)) = line_color;
}


void wait_for_vsync() {
	volatile int *pixel_ctrl_ptr = (int*)0xFF203020; // pixel controller
	register int status;
	
	*pixel_ctrl_ptr = 1; // start the synchronization process
	
	do {
		status = *(pixel_ctrl_ptr + 3);
	} while ((status & 0x01) != 0); // wait for s to become 0
}


// clearing the screen
void clear_screen() {
	for (int i = 0; i < 320; i++) {
		for (int j = 0; j < 240; j++) {
			plot_pixel(i, j, 0);
		}
	}
}


short int get_pixel(int x, int y) {
	return *(short int *)(*(int *)0xFF203020 + (y << 10) + (x << 1));
}


void draw_char(int x, int y, char character) {
	volatile int charBuffer = 0xc9000000;
	*(char *)(charBuffer + (y << 7) + x) = character;
}
 

void write_string(int x, int y, char str[]) {
	for (int i = 0; i < strlen(str); i++) {
		draw_char(x + i, y, str[i]);
	}
}


void draw_map(bool (*map)[COL]) {
	clear_screen();
	for (int i = 0; i < ROW; i++) {
		for (int j = 0; j < COL; j++) {
			if (map[i][j]) {
				// draw the bricks
				for (int x = 45 * j + 5; x < 45 * j + 45; x++) {
					for (int y = 15 * (i + 1) + 5; y < 15 * (i + 1) + 15; y++) {
						plot_pixel(x, y, row_colours[i]);
					}
				}
			}
		}
	}
}


void draw_layout(volatile int *ptr) {
	write_string(30, 40, "                             ");
	write_string(2, 2,"                                                                            ");
	draw_map(play_map);
	wait_for_vsync();
	pixel_buffer_start = *(ptr + 1);
	draw_map(play_map);
	wait_for_vsync();
	pixel_buffer_start = *(ptr + 1);
}


void clear_all_text() {
	for (int x = 0; x < 80; x++) {
		for (int y = 0; y < 60; y++) {
			draw_char(x, y, ' ');
		}
	}
}

int score_add(short int colour) {
    if(colour == row_colours[0]) {
		return 40;
	}
    else if(colour == row_colours[1]) {
		return 30;
	} 
    else if(colour == row_colours[2]) {
		return 30;
	} 
    else if(colour == row_colours[3]) {
		return 20;
	} 
    else if(colour == row_colours[4]) {
		return 20;
	}
    else if(colour == row_colours[5]) {
		return 10;
	}
    else if(colour == row_colours[6]) {
		return 10;
	}
    else if(colour == row_colours[7]) {
		return 5;
	}
    else {
		return 0;
	}
}


