void get_key(unsigned char *key) {
	volatile int * PS2_ptr = (int *) 0xFF200100;
	int data = *PS2_ptr;
	*key = data & 0xFF;

	while (data & 0x8000) {
		data = *PS2_ptr;
	}
}


void draw_paddle(const Paddle *paddle, short int colour) {
	for (int i = paddle->x - paddle->width/2; i < paddle->x + paddle->width/2; i++) {
		for (int j = paddle->y - paddle->height/2; j < paddle->y + paddle->height/2; j++) {
			plot_pixel(i, j, colour);
		}
	}
}


void update_paddle(Paddle *paddle, unsigned char character) {
	if (character == 0x74) {
		paddle->dx = 3;
	} else if (character == 0x6B) {
		paddle->dx = -3;
	} else {
		paddle->dx = 0;
	}
	paddle->x = paddle->x + paddle->dx;
	if (paddle->x - paddle->width/2 < 0 || paddle->x + paddle->width/2 > 320) {
		paddle->x = paddle->x - paddle->dx;
		paddle->dx = 0;
	}
}


void draw_ball(const Ball *ball, short int colour) {
	for (int i = ball->x - ball->width/2; i < ball->x + ball->width/2; i++) {
		for (int j = ball->y - ball->width/2; j < ball->y + ball->width/2; j++) {
			plot_pixel(i, j, colour);
		}
	}
}


void update_ball(Ball *ball, const Paddle *paddle, bool (*map)[COL]){
	int l_side = ball->x - ball->width/2; //left boundary of ball
	int r_side = ball->x + ball->width/2; //right boundary of ball
	int t_side = ball->y - ball->width/2; //top boundary of ball
	int b_side = ball->y + ball->width/2; //bottom boundary of ball

	ball->x = ball->x + ball->dx;
	ball->y = ball->y + ball->dy;

	if(b_side >= 240){// if the ball hits the bottom boundary of screen(paddle misses the ball)
		if(ball->life == 0){ // no life 
			state = lose; 
			return;
		}
		ball->life -= 1; //lose a life
		reset(ball);
		state = respawn;
        write_string(30, 30, "press space key to respawn");
		return;
	}
	if(l_side < 0 || r_side >= 320){ // if the ball hits the left/right boundary of screen
        ball->x = ball->x - 2*ball->dx;
		ball->dx *= -1; //changes x direction
		return;
	} else if(t_side < 15){// if the ball hits the top boundary of screen
        ball->y = ball->y - 2* ball->dy;
		ball->dy *= -1; //changes y direction
		return;
	}

	//check_for_player_collision -> collide_paddle
	//if the ball collides the paddle 
	if (collide_paddle(l_side, b_side, paddle) || collide_paddle(r_side, b_side, paddle)) {
            ball->y = ball->y - 2* ball->dy;//move the ball away from the paddle
			ball->dy *= -1;
			if (paddle->dx != 0) {
				if (paddle->dx * ball->dx < 0) {//if paddle and ball moves in opposite direction
					ball->dx *= -1; //reverse x moving direction for ball 
				}
			}
			return;
	}	

	if(collide_brick(l_side, t_side, ball, map)){ //check top left corner of ball
		if(collide_brick(l_side, t_side - ball->dy, ball, map)){ //if there is no space for ball moving in orignal y direction
			ball->dx *= -1;
        }else{
		    ball->dy *= -1;
        }
		return;
	}

	if(collide_brick(l_side, b_side, ball, map)){ //check bottom left corner of ball
		if(collide_brick(l_side, b_side - ball->dy, ball, map)){
            ball->x = ball->x - 2*ball->dx;
			ball->dx *= -1;
        }else{
            ball->y = ball->y - 2*ball->dy;
		    ball->dy *= -1;
        }
		return;
	}	

	if(collide_brick(r_side, t_side, ball, map)){ //check top right corner of ball
		if(collide_brick(r_side, t_side - ball->dy, ball, map)){
            ball->x = ball->x - 2*ball->dx;
			ball->dx *= -1;
        }else{
            ball->y = ball->y - 2*ball->dy;
		    ball->dy *= -1;
        }
		return;
	}	

	if(collide_brick(r_side, b_side, ball, map)){ //check bottom right corner of ball
		if(collide_brick(r_side, b_side - ball->dy, ball, map)){
            ball->x = ball->x - 2*ball->dx;
			ball->dx *= -1;
        }else{
            ball->y = ball->y - 2*ball->dy;
		    ball->dy *= -1;
        }
		return;
	}

}

void reset(Ball *ball) {
	ball->x = 159;
	ball->y = 220;
	ball->dx = -1;
	ball->dy = 4;
}

bool collide_brick(int x, int y, Ball *ball, bool (*map)[COL]) { //check if ball collides with bricks
	short int colliding_pixel = get_pixel(x, y);

	if (colliding_pixel != (short int)0xFFFF && colliding_pixel != (short int)0x0 && colliding_pixel != (short int)0xF81F) {
		int row_index = y/15 - 1;
		int col_index = x/45;
		break_brick(row_index, col_index, map);
		brick_broke = true;
		broken_brick[0] = row_index;
		broken_brick[1] = col_index;
		ball->touched_pixel = colliding_pixel;
		ball->bricks_broken += 1;
		return true;
	}
	return false;
}


bool collide_paddle(int x, int y, const Paddle *paddle) {
	if (get_pixel(x,y) == (short int)0xFFDF) {
		return true;
	}
	return false;
}


void break_brick(int row, int col, bool (*map)[COL]) {
	map[row][col] = 0;
	for (int x = 45 * col + 5; x < 45 * col + 45; x++) {
		for (int y = 15 * (row + 1) + 5; y < 15 *(row + 1) + 15; y++) {
			plot_pixel(x, y, 0);
		}
	}
}


bool break_all(bool (*map)[COL]) {
	for (int i = 0; i < ROW; i++) {
		for (int j = 0; j < COL; j++) {
			if (map[i][j]) {
				return false;
			}
		}
	}
	return true;
}


void draw_letter(int letter[7][7], int colour, int x, int y, int size){
	for (int i = 0; i < 7; i ++){
		for (int j = 0; j < 7; j ++){
			if (letter[i][j]==1){
				draw_box(y + (size * j),x + (size * i),colour);
			}
		}
	}
}

void draw_box(int x, int y, int colour){
	for (int i = x - 2; i < x + 3; i++){ // 5*5 box
		for (int j = y - 2; j < y + 3; j++){
			plot_pixel(i,j,colour);
		}
	}
}

//2 elements in colour_map corresponds to 1 pixel
//colour = LSR the element 8 spaces, and add the next element
void draw_sprite(int x, int y, int x_size, int y_size, const short int colour_map[]){
    int y_cd, x_cd;
    short int cl; 
    int index = 0;
    
    for (x_cd = x; x_cd < x_size + x; x_cd = x_cd + 1) {
        for (y_cd = y; y_cd < y_size + y; y_cd = y_cd + 1) {
			if (colour_map[index] != 0xff && colour_map[index] != 0xf7) cl = (colour_map[index + 1] << 8) + colour_map[index];
			else cl == 0x00000;
            
            if(y_cd >= 0 && y_cd <= 305 && x_cd >= 15 && x_cd <= 225 && cl != 0) {
				plot_pixel(y_cd, x_cd, cl);
            }
            index = index + 2;
        }
    }
}